#include <string>\n#include <vector>\n#include <algorithm>\n#include <iostream>\n\nstruct Book {\n    std::string isbn;\n    std::string name;\n    std::string author;\n    double price;\n    int borrowInterval; // in days\n\n    Book(const std::string& i, const std::string& n, const std::string& a, double p, int b)\n        : isbn(i), name(n), author(a), price(p), borrowInterval(b) {}\n};\n\nclass Library {\n    std::vector<Book> books;\npublic:\n    void addBook(const Book& book) {\n        books.push_back(book);\n    }\n    bool removeBookByISBN(const std::string& isbn) {\n        auto it = std::remove_if(books.begin(), books.end(), [&](const Book& b){ return b.isbn == isbn; });\n        if (it != books.end()) {\n            books.erase(it, books.end());\n            return true;\n        }\n        return false;\n    }\n    Book* findBookByISBN(const std::string& isbn) {\n        auto it = std::find_if(books.begin(), books.end(), [&](const Book& b){ return b.isbn == isbn; });\n        return it != books.end() ? &(*it) : nullptr;\n    }\n    void listBooks() const {\n        for (const auto& b : books) {\n            std::cout << \"ISBN: \" << b.isbn\n                      << \", Name: \" << b.name\n                      << \", Author: \" << b.author\n                      << \", Price: \" << b.price\n                      << \", Borrow Interval: \" << b.borrowInterval << \" days\" << std::endl;\n        }\n    }\n};